int isValid(char * s){
    char *stack;
    int i, len, j=0;

    len = strlen(s);
    if (len % 2 != 0)
        return 0;

    stack = (char *)malloc((len+1) * sizeof(char));
    if (stack == NULL)
    {
        printf("stack malloc failed. \n");
    }
    memset(stack, '\0', (len+1));

    for (i = 0; i < strlen(s); i++)
    {
        len = strlen(stack);
        // printf("s[%d] = %c stack[%d] = %c \n", i, s[i], j, stack[j]);
        if (len == 0)
        {
            j = 0;
            stack[j] = s[i];
        }
        else{
            if (((stack[j] == '(') && (s[i] == ')')) || ((stack[j] == '[') && (s[i] == ']')) || ((stack[j] == '{') && (s[i] == '}')))
            {
                stack[j] = '\0';
                j--;
            }
            else{
                j++;
                stack[j] = s[i];
            }
        }
        // printf("stack~ = %s \n", stack);
    }

    len = strlen(stack);
    // printf("stack = %s\n", stack);

    free(stack);
    stack = NULL;

    return len == 0 ? 1 : 0;
}